$(function(){$('input[type=file]').each(function(i,elem){if(typeof $(this).attr('data-bfi-disabled')!='undefined'){return;}
var buttonWord='Browse';if(typeof $(this).attr('title')!='undefined'){buttonWord=$(this).attr('title');}
var input=$('<div>').append($(elem).eq(0).clone()).html();$(elem).replaceWith('<a class="file-input-wrapper btn">'+buttonWord+input+'</a>');}).promise().done(function(){$('.file-input-wrapper').mousemove(function(cursor){var input,wrapper,wrapperX,wrapperY,inputWidth,inputHeight,cursorX,cursorY;wrapper=$(this);input=wrapper.find("input");wrapperX=wrapper.offset().left;wrapperY=wrapper.offset().top;inputWidth=input.width();inputHeight=input.height();cursorX=cursor.pageX;cursorY=cursor.pageY;moveInputX=cursorX-wrapperX-inputWidth+20;moveInputY=cursorY-wrapperY-(inputHeight/2);input.css({left:moveInputX,top:moveInputY});});$('.file-input-wrapper input[type=file]').change(function(){$(this).parent().next().has('file-input-name').remove();$(this).parent().after('<span class="file-input-name">'+$(this).val()+'</span>');});});var cssHtml='<style>'+
'.file-input-wrapper { overflow: hidden; position: relative; cursor: pointer; z-index: 1; }'+
'.file-input-wrapper input[type=file], .file-input-wrapper input[type=file]:focus, .file-input-wrapper input[type=file]:hover { position: absolute; top: 0; left: 0; cursor: pointer; opacity: 0; filter: alpha(opacity=0); z-index: 99; outline: 0; }'+
'.file-input-name { margin-left: 8px; }'+
'</style>';$('link[rel=stylesheet]').eq(0).before(cssHtml);});